import { useEffect } from 'react'
import { useRef } from 'react'
import { useLocation } from 'react-router-dom/cjs/react-router-dom.min'

export default function ScrollToTop() {

  const {pathname}=useLocation
  useEffect(
    () => {
      window.scrollTo(0, 0)
    },
    [],
  )

  return null
}
